import { Header } from "../components/Header"
import axios from "axios"
import { useEffect, useState } from "react"
import { TrainTable } from "../components/TrainTable"

export const MainSite=()=>{
    const [data,setData]=useState([])
    useEffect(()=>{
        axios.get("http://localhost:8080/trains/alltrains").then((response)=>{
            setData(response.data)
        }).catch(error=>{
            alert(error)
        })
    })
    
    return(
        <div className="h-screen bg-slate-200">
            <Header></Header>
                <p className="text-3xl mt-3 ml-5 mb-5">Az adatbázisban lévő vonatok</p>
                <table className="table-auto m-auto text-center">
                    <thead className="border-b">
                        <tr>
                            <th>Sorozatszám</th>
                            <th>Becenév</th>
                            <th>Gyártó</th>
                            <th>Maximális engedélyezett sebesség</th>
                        </tr>
                    </thead>
                    <tbody>{TrainTable(data)}</tbody>
                </table>
        </div>
    )
}
